import React, { useEffect, useState } from 'react'
import './WardenDashboard.css'
import '../styles/status.css'

export default function WardenDashboard() {
  const [complaints, setComplaints] = useState([])
  const [messages, setMessages] = useState({})
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    loadAll()
  }, [])

  async function loadAll() {
    setLoading(true)
    try {
      const res = await fetch('http://localhost:5000/complaints')
      const data = await res.json()
      setComplaints(Array.isArray(data) ? data : [])
    } catch {}

    setLoading(false)
  }

  function onMsg(id, v) {
    setMessages(prev => ({ ...prev, [id]: v }))
  }

  async function decide(id, decision) {
    const msg = messages[id] || ''
    try {
      const res = await fetch(`http://localhost:5000/complaints/${id}/decision`, {
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body:JSON.stringify({decision, warden_message:msg})
      })
      const data = await res.json()
      if(!res.ok) throw new Error()
      setComplaints(prev => prev.map(x => x.id === data.id ? data : x))
    } catch {}
  }

  return (
    <div className="warden-wrap">
      <div className="card" style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
        <h2>Warden Dashboard</h2>
        <button className="btn small" onClick={loadAll}>
          {loading ? 'Refreshing...' : 'Refresh'}
        </button>
      </div>

      <div className="warden-list card">
        {complaints.map(c => (
          <div className="warden-item" key={c.id}>
            <div className="wi-left">
              <div><strong>{c.student_id}</strong></div>
              <div>Category: {c.complaint_category}</div>
              <div>Message: {c.message}</div>

              {c.image && (
                <img src={c.image} alt="" style={{width:140,borderRadius:10,marginTop:8}} />
              )}

              {c.worker_updates && (
                <ul>
                  {c.worker_updates.map((u,i)=>(
                    <li key={i}>{u.worker_id}: {u.message}</li>
                  ))}
                </ul>
              )}
            </div>

            <div className="wi-right">
              <span className={`status-badge status-${c.status}`}>{c.status}</span>

              <input 
                className="input"
                placeholder="Message"
                value={messages[c.id] || ''}
                onChange={e=>onMsg(c.id,e.target.value)}
                style={{marginTop:10}}
              />

              <button className="btn small" onClick={()=>decide(c.id,'accepted')}>Accept</button>
              <button className="btn small" style={{background:'#F44336'}} onClick={()=>decide(c.id,'rejected')}>Reject</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}
