import React, { useEffect, useState } from 'react'
import { getAuth } from '../utils/auth'
import './StudentDashboard.css'
import '../styles/status.css'

export default function StudentDashboard() {
  const user = getAuth()
  const [complaints, setComplaints] = useState([])
  const [form, setForm] = useState({
    complaint_category: 'plumbing',
    message: '',
    image_url: ''
  })
  const [editing, setEditing] = useState(null)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState('')

  useEffect(() => {
    if (!user) {
      setComplaints([])
      return
    }
    setLoading(true)
    fetch(`http://localhost:5000/complaints/student/${encodeURIComponent(user.id)}`)
      .then(r => r.json())
      .then(data => {
        setComplaints(Array.isArray(data) ? data : [])
      })
      .catch(() => setComplaints([]))
      .finally(() => setLoading(false))
  }, [user])

  function onChange(e) {
    const { name, value } = e.target
    setForm(prev => ({ ...prev, [name]: value }))
  }

  async function createComplaint(e) {
    e.preventDefault()
    setError('')
    if (!user) return setError('Not logged in')
    if (!form.message.trim()) return setError('Message required')
    try {
      const res = await fetch('http://localhost:5000/complaints', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ student_id: user.id, ...form })
      })
      const data = await res.json()
      if (!res.ok) throw new Error(data.message || 'Failed')
      setComplaints(prev => [data, ...prev])
      setForm({ complaint_category: 'plumbing', message: '', image_url: '' })
    } catch (err) {
      setError(err.message || 'Network error')
    }
  }

  function startEdit(c) {
    setEditing(c)
    setForm({
      complaint_category: c.complaint_category || 'plumbing',
      message: c.message || '',
      image_url: c.image_url || ''
    })
    window.scrollTo({ top: 0, behavior: 'smooth' })
  }

  async function updateComplaint(e) {
    e.preventDefault()
    setError('')
    if (!editing) return
    try {
      const res = await fetch(`http://localhost:5000/complaints/${editing.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      })
      const data = await res.json()
      if (!res.ok) throw new Error(data.message || 'Failed to update')
      setComplaints(prev => prev.map(p => (p.id === data.id ? data : p)))
      setEditing(null)
      setForm({ complaint_category: 'plumbing', message: '', image_url: '' })
    } catch (err) {
      setError(err.message || 'Network error')
    }
  }

  function cancelEdit() {
    setEditing(null)
    setForm({ complaint_category: 'plumbing', message: '', image_url: '' })
    setError('')
  }

  return (
    <div className="student-wrap">
      <div className="student-left card">
        <h3>Student Dashboard</h3>
        <p className="small">Logged in as {user ? user.id : 'Guest'}</p>

        <h4>{editing ? 'Edit Complaint' : 'New Complaint'}</h4>

        <form onSubmit={editing ? updateComplaint : createComplaint} className="complaint-form">
          <label>Category</label>
          <div className="category-list" role="list">
            <button
              type="button"
              className={form.complaint_category === 'plumbing' ? 'pill active' : 'pill'}
              onClick={() => setForm(prev => ({ ...prev, complaint_category: 'plumbing' }))}
            >
              Plumbing
            </button>
            <button
              type="button"
              className={form.complaint_category === 'carpentry' ? 'pill active' : 'pill'}
              onClick={() => setForm(prev => ({ ...prev, complaint_category: 'carpentry' }))}
            >
              Carpentry
            </button>
            <button
              type="button"
              className={form.complaint_category === 'electricity' ? 'pill active' : 'pill'}
              onClick={() => setForm(prev => ({ ...prev, complaint_category: 'electricity' }))}
            >
              Electricity
            </button>
            <button
              type="button"
              className={form.complaint_category === 'other' ? 'pill active' : 'pill'}
              onClick={() => setForm(prev => ({ ...prev, complaint_category: 'other' }))}
            >
              Other
            </button>
          </div>

          <label>Image URL (optional)</label>
          <input name="image_url" className="input" value={form.image_url} onChange={onChange} />

          <label>Message</label>
          <textarea name="message" className="input" value={form.message} onChange={onChange} />

          <div className="form-actions">
            <button className="btn" type="submit">{editing ? 'Update' : 'Submit'}</button>
            {editing && <button type="button" className="btn outline" onClick={cancelEdit}>Cancel</button>}
          </div>

          {error && <div className="small" style={{ color: '#b91c1c', marginTop: 8 }}>{error}</div>}
        </form>
      </div>

      <div className="student-right card">
        <h4>My Complaints</h4>

        {loading && <div className="small muted">Loading...</div>}

        <table className="complaint-table" aria-live="polite">
          <thead>
            <tr>
              <th>Category</th>
              <th>Message</th>
              <th>Status</th>
              <th>Created</th>
              <th>Action</th>
            </tr>
          </thead>

          <tbody>
            {complaints.map(c => (
              <tr key={c.id}>
                <td>{c.complaint_category}</td>
                <td className="msg-td">{c.message}</td>
                <td>
                  <span className={`status-badge status-${c.status}`}>
                    {c.status}
                  </span>
                </td>
                <td>{new Date(c.createdAt || c.updatedAt || Date.now()).toLocaleString()}</td>
                <td>
                  {c.status === 'pending' ? (
                    <button className="btn small" onClick={() => startEdit(c)}>Edit</button>
                  ) : (
                    <span className="small muted">â€”</span>
                  )}
                </td>
              </tr>
            ))}

            {complaints.length === 0 && !loading && (
              <tr>
                <td colSpan="5" className="small muted">No complaints yet</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  )
}
