import React, { useEffect, useState } from 'react'
import { getAuth } from '../utils/auth'
import './WorkerDashboard.css'
import '../styles/status.css'

export default function WorkerDashboard() {
  const user = getAuth()
  const [complaints, setComplaints] = useState([])
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    loadAssigned()
  }, [])

  async function loadAssigned() {
    setLoading(true)
    try {
      const res = await fetch('http://localhost:5000/complaints?status=accepted')
      const data = await res.json()
      setComplaints(Array.isArray(data) ? data : [])
    } catch {
      setComplaints([])
    }
    setLoading(false)
  }

  async function markCompleted(id) {
    const message = `Completed by ${user.id}`
    try {
      const res = await fetch(`http://localhost:5000/complaints/${id}/update`, {
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body:JSON.stringify({worker_id:user.id, update_message:message})
      })
      const data = await res.json()
      if(res.ok){
        setComplaints(prev => prev.map(x => x.id === data.id ? data : x))
      }
    } catch {}
  }

  return (
    <div className="worker-wrap">
      <div className="card" style={{display:'flex',justifyContent:'space-between'}}>
        <h3>Worker Dashboard</h3>
        <button className="btn small" onClick={loadAssigned}>
          {loading ? 'Refreshing...' : 'Refresh'}
        </button>
      </div>

      <div className="worker-list card">
        {complaints.map(c => (
          <div className="worker-item" key={c.id}>
            <div className="wi-left">
              <div>Student: {c.student_id}</div>
              <div>Category: {c.complaint_category}</div>
              <div>Message: {c.message}</div>

              {c.image && (
                <img src={c.image} alt="" style={{width:140,borderRadius:10,marginTop:8}} />
              )}

              {c.worker_updates && (
                <ul>
                  {c.worker_updates.map((u,i)=>(
                    <li key={i}>{u.message}</li>
                  ))}
                </ul>
              )}
            </div>

            <div className="wi-right">
              <span className={`status-badge status-${c.status}`}>{c.status}</span>

              {c.status === 'accepted' && (
                <button className="btn small" style={{marginTop:10}} onClick={()=>markCompleted(c.id)}>
                  Completed
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}
