// frontend/src/pages/WardenDashboard.js
import React, { useEffect, useState } from 'react'
import './WardenDashboard.css'
import '../styles/status.css'

export default function WardenDashboard() {
  const [complaints, setComplaints] = useState([])
  const [filter, setFilter] = useState({ status: 'all', category: 'all' })
  const [loading, setLoading] = useState(false)
  const [messages, setMessages] = useState({}) // draft warden msgs per complaint

  useEffect(() => {
    loadAll()
  }, [])

  async function loadAll() {
    setLoading(true)
    try {
      const res = await fetch('http://localhost:5000/complaints')
      const data = await res.json()
      setComplaints(Array.isArray(data) ? data : [])
    } catch (err) {
      setComplaints([])
    } finally {
      setLoading(false)
    }
  }

  function onMessageChange(id, value) {
    setMessages(prev => ({ ...prev, [id]: value }))
  }

  async function decide(id, decision) {
    const warden_message = messages[id] || ''
    try {
      const res = await fetch(`http://localhost:5000/complaints/${id}/decision`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ decision, warden_message })
      })
      const data = await res.json()
      if (res.ok) {
        setComplaints(prev => prev.map(p => (p.id === data.id ? data : p)))
        setMessages(prev => { const copy = { ...prev }; delete copy[id]; return copy })
      } else {
        alert(data.message || 'Failed')
      }
    } catch (err) {
      alert('Network error')
    }
  }

  function filtered() {
    return complaints.filter(c => {
      if (filter.status !== 'all' && c.status !== filter.status) return false
      if (filter.category !== 'all' && c.complaint_category !== filter.category) return false
      return true
    })
  }

  function buildActivity(c) {
    const acts = []
    acts.push({ time: c.createdAt || c._createdAt || null, text: `Complaint created by ${c.student_id}` })
    if (c.warden_message) acts.push({ time: c.updatedAt || null, text: `Warden note: ${c.warden_message}` })
    if (c.worker_updates && Array.isArray(c.worker_updates)) {
      c.worker_updates.forEach((u, i) => {
        acts.push({ time: u.time || null, text: `Worker ${u.worker_id}: ${u.message}` })
      })
    }
    acts.push({ time: c.status ? null : null, text: `Current status: ${c.status}` })
    return acts
  }

  return (
    <div className="warden-wrap">
      <div className="warden-top card">
        <h2>Warden — Admin Panel</h2>
        <p className="small">You see all complaints and activities across hostels</p>

        <div className="filters">
          <div className="filter-group">
            <label>Status</label>
            <select value={filter.status} onChange={e => setFilter({ ...filter, status: e.target.value })} className="input">
              <option value="all">All</option>
              <option value="pending">Pending</option>
              <option value="accepted">Accepted</option>
              <option value="resolved">Resolved</option>
              <option value="rejected">Rejected</option>
            </select>
          </div>

          <div className="filter-group">
            <label>Category</label>
            <select value={filter.category} onChange={e => setFilter({ ...filter, category: e.target.value })} className="input">
              <option value="all">All</option>
              <option value="plumbing">Plumbing</option>
              <option value="electricity">Electricity</option>
              <option value="carpentry">Carpentry</option>
              <option value="other">Other</option>
            </select>
          </div>

          <div className="filter-actions">
            <button className="btn" onClick={loadAll}>Refresh</button>
          </div>
        </div>
      </div>

      <div className="warden-grid">
        <div className="list-panel card">
          <h3>All Complaints ({complaints.length})</h3>
          {loading && <div className="small muted">Loading...</div>}
          <div className="complaint-list">
            {filtered().map(c => (
              <div className="complaint-card" key={c.id}>
                <div className="cc-left">
                  <div className="cc-top">
                    <div className="cc-title">
                      <strong>{c.complaint_category}</strong>
                      <div className="cc-meta small">{c.student_id} • Room: {c.room || '-'}</div>
                    </div>
                    <div className="cc-status">
                      <span className={`status-badge status-${c.status}`}>{c.status}</span>
                    </div>
                  </div>

                  <div className="cc-msg">{c.message}</div>

                  {c.image_url && (
                    <div className="cc-image">
                      <img src={c.image_url} alt="attachment" />
                    </div>
                  )}

                  <div className="cc-worker-updates">
                    {c.worker_updates && c.worker_updates.length > 0 && (
                      <div>
                        <strong>Worker updates:</strong>
                        <ul>
                          {c.worker_updates.map((u, i) => <li key={i}>{u.worker_id}: {u.message}</li>)}
                        </ul>
                      </div>
                    )}
                  </div>
                </div>

                <div className="cc-right">
                  <input
                    className="input warden-input"
                    placeholder="Optional message for worker"
                    value={messages[c.id] || ''}
                    onChange={e => onMessageChange(c.id, e.target.value)}
                  />
                  <div className="cc-actions">
                    <button className="btn" onClick={() => decide(c.id, 'accepted')}>Accept</button>
                    <button className="btn reject" onClick={() => decide(c.id, 'rejected')}>Reject</button>
                  </div>
                  <div className="activity">
                    <strong>Activity</strong>
                    <div className="activity-list">
                      {buildActivity(c).map((a, idx) => (
                        <div key={idx} className="activity-item">
                          <div className="act-text">{a.text}</div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            ))}
            {filtered().length === 0 && !loading && <div className="small muted">No complaints found</div>}
          </div>
        </div>

        <div className="overview-panel card">
          <h3>Overview</h3>
          <div className="overview-stats">
            <div className="stat">
              <div className="stat-number">{complaints.filter(c => c.status === 'pending').length}</div>
              <div className="stat-label">Pending</div>
            </div>
            <div className="stat">
              <div className="stat-number">{complaints.filter(c => c.status === 'accepted').length}</div>
              <div className="stat-label">Accepted</div>
            </div>
            <div className="stat">
              <div className="stat-number">{complaints.filter(c => c.status === 'resolved' || c.status === 'completed').length}</div>
              <div className="stat-label">Completed</div>
            </div>
            <div className="stat">
              <div className="stat-number">{complaints.filter(c => c.status === 'rejected').length}</div>
              <div className="stat-label">Rejected</div>
            </div>
          </div>

          <h4 style={{marginTop:16}}>Recent Activities</h4>
          <div className="recent-activities">
            {complaints.slice().reverse().slice(0,8).map(c => (
              <div key={c.id} className="recent-item">
                <div className="recent-title">{c.complaint_category} — {c.student_id}</div>
                <div className="recent-sub small">{c.status} • {new Date(c.createdAt || Date.now()).toLocaleString()}</div>
              </div>
            ))}
            {complaints.length === 0 && <div className="small muted">No recent activity</div>}
          </div>
        </div>
      </div>
    </div>
  )
}
