import React, { useEffect, useState } from 'react'
import { getAuth } from '../utils/auth'
import './WorkerDashboard.css'

export default function WorkerDashboard(){
  const user = getAuth()
  const [complaints, setComplaints] = useState([])

  useEffect(()=> {
    fetch('http://localhost:5000/complaints?status=accepted')
      .then(r=>r.json()).then(d=>setComplaints(d)).catch(()=>setComplaints([]))
  }, [])

  function countStatus(status){
    return complaints.filter(c=>c.status===status).length
  }

  async function addUpdate(id){
    const text = prompt('Add update/status message')
    if(!text) return
    const res = await fetch(`http://localhost:5000/complaints/${id}/update`, {
      method:'POST', headers:{'Content-Type':'application/json'}, body:JSON.stringify({ worker_id:user.id, update_message:text })
    })
    const data = await res.json()
    if(res.ok) setComplaints(prev=>prev.map(p=>p.id===data.id?data:p))
  }

  return (
    <div className="worker-wrap">
      <div className="worker-top card">
        <h3>Worker Dashboard</h3>
        <div className="worker-info">
          <div className="worker-name">{user ? user.id : ''}</div>
          <div className="worker-stats"><button className="stat-btn">Pending ({countStatus('accepted')})</button><button className="stat-btn">Completed ({countStatus('resolved')})</button></div>
        </div>
      </div>

      <div className="worker-list card">
        <h4>Assigned Complaints</h4>
        {complaints.length===0 && <div className="small muted">No assigned complaints</div>}
        {complaints.map(c=>(
          <div className="worker-item" key={c.id}>
            <div className="wi-left">
              <div><strong>Student:</strong> {c.student_id}</div>
              <div><strong>Room:</strong> {c.room || '-'}</div>
              <div><strong>Category:</strong> {c.complaint_category}</div>
              <div><strong>Message:</strong> {c.message}</div>
            </div>
            <div className="wi-right">
              <div className="status">{c.status}</div>
              <button className="btn" onClick={()=>addUpdate(c.id)}>Add Update</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}
