import React, { useEffect, useState } from 'react'
import './WardenDashboard.css'

export default function WardenDashboard(){
  const [complaints, setComplaints] = useState([])
  const [filter, setFilter] = useState({ status:'all', category:'all' })
  const [messages, setMessages] = useState({}) // { [complaintId]: messageString }

  useEffect(()=> {
    fetch('http://localhost:5000/complaints')
      .then(r=>r.json()).then(d=>setComplaints(d)).catch(()=>setComplaints([]))
  }, [])

  function filtered(){
    return complaints.filter(c=>{
      if(filter.status!=='all' && c.status!==filter.status) return false
      if(filter.category!=='all' && c.complaint_category!==filter.category) return false
      return true
    })
  }

  function onMessageChange(id, value){
    setMessages(prev => ({ ...prev, [id]: value }))
  }

  async function decide(id, decision){
    const warden_message = messages[id] || ''
    try {
      const res = await fetch(`http://localhost:5000/complaints/${id}/decision`, {
        method:'POST',
        headers:{ 'Content-Type':'application/json' },
        body: JSON.stringify({ decision, warden_message })
      })
      const data = await res.json()
      if (res.ok) {
        setComplaints(prev => prev.map(p => (p.id === data.id ? data : p)))
        setMessages(prev => { const copy = { ...prev }; delete copy[id]; return copy })
      } else {
        console.error('Decision failed', data)
        alert(data.message || 'Failed to update decision')
      }
    } catch (err) {
      console.error(err)
      alert('Network error')
    }
  }

  return (
    <div className="warden-wrap">
      <div className="warden-top card">
        <h3>Warden Dashboard</h3>
        <p className="small">Manage complaints for your hostel block.</p>
        <div className="filters">
          <button className="filter-btn" onClick={()=>setFilter({...filter, status:'all'})}>All Status</button>
          <button className="filter-btn" onClick={()=>setFilter({...filter, status:'pending'})}>Pending</button>
          <button className="filter-btn" onClick={()=>setFilter({...filter, status:'accepted'})}>Accepted</button>
          <select onChange={(e)=>setFilter({...filter, category:e.target.value})} className="input">
            <option value="all">All Categories</option>
            <option value="plumbing">Plumbing</option>
            <option value="electricity">Electricity</option>
            <option value="carpentry">Carpentry</option>
          </select>
        </div>
      </div>

      <div className="block-list card">
        <h4>Block Complaints</h4>
        <table className="block-table">
          <thead><tr><th>Student</th><th>Room</th><th>Category</th><th>Message</th><th>Status</th><th>Assigned Worker</th><th>Actions</th></tr></thead>
          <tbody>
            {filtered().map(c=>(
              <tr key={c.id}>
                <td>{c.student_id}</td>
                <td>{c.room || '-'}</td>
                <td>{c.complaint_category}</td>
                <td className="msg-td">{c.message}</td>
                <td>{c.status}</td>
                <td>{c.assigned_worker || '-'}</td>
                <td style={{minWidth:260}}>
                  <div style={{display:'flex',flexDirection:'column',gap:8}}>
                    <input
                      value={messages[c.id] || ''}
                      onChange={(e)=>onMessageChange(c.id, e.target.value)}
                      placeholder="Optional message for worker"
                      className="input warden-input"
                    />
                    <div style={{display:'flex',gap:8}}>
                      <button className="btn" onClick={()=>decide(c.id,'accepted')}>Accept</button>
                      <button className="btn reject" onClick={()=>decide(c.id,'rejected')}>Reject</button>
                    </div>
                  </div>
                </td>
              </tr>
            ))}
            {filtered().length===0 && <tr><td colSpan="7" className="small muted">No complaints</td></tr>}
          </tbody>
        </table>
      </div>
    </div>
  )
}
