import React, { useState, useEffect } from 'react'
import { getAuth } from '../utils/auth'
import './StudentDashboard.css'

export default function StudentDashboard(){
  const user = getAuth()
  const [complaints, setComplaints] = useState([])
  const [form, setForm] = useState({ complaint_category:'plumbing', message:'', image_url:'' })
  const [editing, setEditing] = useState(null)

  useEffect(()=>{
    if(!user) return
    fetch(`http://localhost:5000/complaints/student/${user.id}`)
      .then(r=>r.json()).then(d=>setComplaints(d)).catch(()=>setComplaints([]))
  }, [])

  function onChange(e){ setForm({...form, [e.target.name]: e.target.value}) }

  async function createComplaint(e){
    e.preventDefault()
    const res = await fetch('http://localhost:5000/complaints', {
      method:'POST', headers:{'Content-Type':'application/json'}, body:JSON.stringify({ student_id:user.id, ...form })
    })
    const data = await res.json()
    if(res.ok) setComplaints(prev=>[...prev, data])
    setForm({ complaint_category:'plumbing', message:'', image_url:'' })
  }

  function startEdit(c){
    setEditing(c)
    setForm({ complaint_category:c.complaint_category, message:c.message, image_url:c.image_url||'' })
  }

  async function updateComplaint(e){
    e.preventDefault()
    const res = await fetch(`http://localhost:5000/complaints/${editing.id}`, {
      method:'PUT', headers:{'Content-Type':'application/json'}, body:JSON.stringify(form)
    })
    const data = await res.json()
    if(res.ok) setComplaints(prev=>prev.map(p=>p.id===data.id?data:p))
    setEditing(null)
    setForm({ complaint_category:'plumbing', message:'', image_url:'' })
  }

  return (
    <div className="student-wrap">
      <div className="student-left card">
        <h3>Student Dashboard</h3>
        <p className="small">Hi {user ? user.id : ''}</p>
        <h4>New Complaint</h4>
        <form onSubmit={editing ? updateComplaint : createComplaint} className="complaint-form">
          <label>Category</label>
          <div className="category-list">
            <button type="button" className={form.complaint_category==='plumbing'?'pill active':'pill'} onClick={()=>setForm({...form, complaint_category:'plumbing'})}>Plumbing</button>
            <button type="button" className={form.complaint_category==='carpentry'?'pill active':'pill'} onClick={()=>setForm({...form, complaint_category:'carpentry'})}>Carpentry</button>
            <button type="button" className={form.complaint_category==='electricity'?'pill active':'pill'} onClick={()=>setForm({...form, complaint_category:'electricity'})}>Electricity</button>
          </div>
          <label>Image URL (optional)</label>
          <input name="image_url" className="input" value={form.image_url} onChange={onChange}/>
          <label>Message</label>
          <textarea name="message" className="input" value={form.message} onChange={onChange}></textarea>
          <div className="form-actions">
            <button className="btn" type="submit">{editing ? 'Update' : 'Submit'}</button>
            <button type="button" className="btn outline" onClick={()=>document.getElementById('about')?.scrollIntoView({behavior:'smooth'})}>Explaire</button>
          </div>
        </form>
      </div>

      <div className="student-right card">
        <h4>My Complaints</h4>
        <table className="complaint-table">
          <thead>
            <tr><th>Category</th><th>Message</th><th>Status</th><th>Created</th><th>Actions</th></tr>
          </thead>
          <tbody>
            {complaints.map(c=>(
              <tr key={c.id}>
                <td>{c.complaint_category}</td>
                <td className="msg-td">{c.message}</td>
                <td>{c.status}</td>
                <td>{new Date(c.createdAt || Date.now()).toLocaleString()}</td>
                <td>{c.status==='pending' && <button className="btn small" onClick={()=>startEdit(c)}>Edit</button>}</td>
              </tr>
            ))}
            {complaints.length===0 && <tr><td colSpan="5" className="small muted">No complaints yet.</td></tr>}
          </tbody>
        </table>
      </div>
    </div>
  )
}
