import React, { useEffect, useState } from 'react'
import './WardenDashboard.css'
export default function WardenDashboard() {
  const [complaints, setComplaints] = useState([])
  useEffect(()=> {
    fetch('http://localhost:5000/complaints')
      .then(r=>r.json()).then(d=>setComplaints(d)).catch(()=>setComplaints([]))
  }, [])
  async function decide(id, decision) {
    const msg = document.getElementById('m-'+id).value
    const res = await fetch(`http://localhost:5000/complaints/${id}/decision`, {
      method: 'POST',
      headers: {'Content-Type':'application/json'},
      body: JSON.stringify({ decision, warden_message: msg })
    })
    const data = await res.json()
    if (res.ok) setComplaints(prev => prev.map(p => p.id === data.id ? data : p))
  }
  return (
    <div className="warden-page">
      <h2>Warden Dashboard</h2>
      <div>
        {complaints.map(c=>(
          <div className="card" key={c.id}>
            <div><strong>Student:</strong> {c.student_id}</div>
            <div><strong>Category:</strong> {c.complaint_category}</div>
            <div><strong>Message:</strong> {c.message}</div>
            <div><strong>Status:</strong> <span className="status">{c.status}</span></div>
            {c.image_url && <img className="complaint-img" src={c.image_url} alt="img" />}
            {c.warden_message && <div><strong>Warden msg:</strong> {c.warden_message}</div>}
            <div className="form-row">
              <input id={'m-'+c.id} placeholder="Optional message for worker" className="input" />
            </div>
            <div className="actions">
              <button className="btn" onClick={()=>decide(c.id, 'accepted')}>Accept</button>
              <button className="btn reject" onClick={()=>decide(c.id, 'rejected')}>Reject</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}
