import React, { useEffect, useState } from 'react'
import { getAuth } from '../utils/auth'
import './WorkerDashboard.css'
export default function WorkerDashboard() {
  const user = getAuth()
  const [complaints, setComplaints] = useState([])
  useEffect(()=> {
    fetch('http://localhost:5000/complaints?status=accepted')
      .then(r=>r.json()).then(d=>setComplaints(d)).catch(()=>setComplaints([]))
  }, [])
  async function addUpdate(id) {
    const txt = document.getElementById('u-'+id).value
    const res = await fetch(`http://localhost:5000/complaints/${id}/update`, {
      method: 'POST',
      headers: {'Content-Type':'application/json'},
      body: JSON.stringify({ worker_id: user.id, update_message: txt })
    })
    const data = await res.json()
    if (res.ok) setComplaints(prev => prev.map(p => p.id === data.id ? data : p))
    document.getElementById('u-'+id).value = ''
  }
  return (
    <div className="worker-page">
      <h2>Worker Dashboard</h2>
      {complaints.map(c=>(
        <div className="card" key={c.id}>
          <div><strong>Student:</strong> {c.student_id}</div>
          <div><strong>Category:</strong> {c.complaint_category}</div>
          <div><strong>Message:</strong> {c.message}</div>
          <div><strong>Status:</strong> <span className="status">{c.status}</span></div>
          {c.warden_message && <div><strong>Warden note:</strong> {c.warden_message}</div>}
          <div className="form-row">
            <input id={'u-'+c.id} placeholder="Update message" className="input" />
            <button className="btn" onClick={()=>addUpdate(c.id)}>Add Update</button>
          </div>
          <div>
            <strong>Updates:</strong>
            {c.worker_updates && c.worker_updates.length>0 ? (
              <ul>
                {c.worker_updates.map((u,i)=>(<li key={i}>{u.worker_id}: {u.message}</li>))}
              </ul>
            ) : <div className="small">No updates</div>}
          </div>
        </div>
      ))}
    </div>
  )
}
