import React, { useState, useEffect } from 'react'
import { getAuth } from '../utils/auth'
import './StudentDashboard.css'
export default function StudentDashboard() {
  const user = getAuth()
  const [complaints, setComplaints] = useState([])
  const [form, setForm] = useState({ complaint_category: 'electricity', message: '', image_url: '' })
  const [editing, setEditing] = useState(null)
  useEffect(() => {
    fetch(`http://localhost:5000/complaints/student/${user.id}`)
      .then(r => r.json()).then(d => setComplaints(d)).catch(() => setComplaints([]))
  }, [])
  function onChange(e) { setForm({ ...form, [e.target.name]: e.target.value }) }
  async function createComplaint(e) {
    e.preventDefault()
    const res = await fetch('http://localhost:5000/complaints', {
      method: 'POST',
      headers: {'Content-Type':'application/json'},
      body: JSON.stringify({ student_id: user.id, ...form })
    })
    const data = await res.json()
    if (res.ok) {
      setComplaints(prev => [...prev, data])
      setForm({ complaint_category: 'electricity', message: '', image_url: '' })
    }
  }
  function startEdit(c) {
    setEditing(c)
    setForm({ complaint_category: c.complaint_category, message: c.message, image_url: c.image_url || '' })
  }
  async function updateComplaint(e) {
    e.preventDefault()
    const res = await fetch(`http://localhost:5000/complaints/${editing.id}`, {
      method: 'PUT',
      headers: {'Content-Type':'application/json'},
      body: JSON.stringify(form)
    })
    const data = await res.json()
    if (res.ok) {
      setComplaints(prev => prev.map(p => p.id === data.id ? data : p))
      setEditing(null)
      setForm({ complaint_category: 'electricity', message: '', image_url: '' })
    }
  }
  return (
    <div className="student-page">
      <h2>Student Dashboard</h2>
      <p className="small">Logged in as {user.id}</p>
      <div className="card">
        <h3>{editing ? 'Edit Complaint' : 'Register a Complaint'}</h3>
        <form onSubmit={editing ? updateComplaint : createComplaint}>
          <div className="form-row"><label>Category</label>
            <select name="complaint_category" value={form.complaint_category} onChange={onChange} className="input">
              <option value="electricity">Electricity</option>
              <option value="plumbing">Plumbing</option>
              <option value="carpentry">Carpentry</option>
              <option value="other">Other</option>
            </select>
          </div>
          <div className="form-row"><label>Message</label><textarea name="message" value={form.message} onChange={onChange} className="input"></textarea></div>
          <div className="form-row"><label>Image URL</label><input name="image_url" value={form.image_url} onChange={onChange} className="input" /></div>
          <div className="form-row"><button className="btn" type="submit">{editing ? 'Update' : 'Submit'}</button>
            {editing && <button type="button" className="btn cancel" onClick={()=>{setEditing(null); setForm({ complaint_category:'electricity', message:'', image_url:'' })}}>Cancel</button>}
          </div>
        </form>
      </div>
      <h3 style={{marginTop:16}}>Your Complaints</h3>
      {complaints.map(c=>(
        <div className="card" key={c.id}>
          <div><strong>Category:</strong> {c.complaint_category}</div>
          <div><strong>Message:</strong> {c.message}</div>
          <div><strong>Status:</strong> <span className="status">{c.status}</span></div>
          {c.image_url && <img className="complaint-img" src={c.image_url} alt="img" />}
          {c.warden_message && <div><strong>Warden:</strong> {c.warden_message}</div>}
          <div className="actions">
            {c.status === 'pending' && <button className="btn" onClick={()=>startEdit(c)}>Edit</button>}
          </div>
        </div>
      ))}
    </div>
  )
}
